jQuery(document).ready(function(){jQuery("time.timeago").timeago()});$(document).ready(function(){$(".js-delete-message").click(function(n){var t=$(n.target);bootbox.dialog({message:"Sure you want to delete the message?",buttons:{no:{label:"No",className:"btn-default",callback:function(){bootbox.hideAll()}},yes:{label:"Yes",className:"btn-danger",callback:function(){$.ajax({url:"/api/v1/message/"+t.attr("message-id"),method:"DELETE"}).done(function(){t.parents("li").fadeOut(function(){$(this).remove()})}).fail(function(){alert("Something failed!")})}}}})});$(".js-archive-message").click(function(n){var t=$(n.target);$.ajax({url:"/api/v1/message/"+t.attr("message-id"),method:"PUT"}).done(function(){t.parents("li").fadeOut(function(){$(this).remove()})}).fail(function(){alert("Something failed!")})});$(".js-report-message").click(function(n){var t=$(n.target);bootbox.prompt({title:"What is your name?",callback:function(n){$.ajax({method:"POST",url:"/api/v1/message/report/"+t.attr("message-id"),data:{id:t.attr("message-id"),reason:n}}).done(function(){console.log("Success")}).fail(function(){alert("Something failed!")})}})});$(".js-delete-role").click(function(n){var t=$(n.target);bootbox.dialog({message:"Sure you want to delete the role?",buttons:{no:{label:"No",className:"btn-default",callback:function(){bootbox.hideAll()}},yes:{label:"Yes",className:"btn-danger",callback:function(){$.ajax({url:"/api/v1/admin/"+t.attr("role-id"),method:"DELETE"}).done(function(){t.parents("tr").fadeOut(function(){$(this).remove()})}).fail(function(){alert("Something failed!")})}}}})})});